server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import express from "express";
import { authController } from "./controllers/authController";
import { fileController } from "./controllers/fileController";
import { analyticsController } from "./controllers/analyticsController";
import { supabaseAuthMiddleware } from "./middleware/supabaseAuth";

export async function registerRoutes(app: Express): Promise<Server> {
  // Middleware
  app.use(express.json());
  app.use(express.urlencoded({ extended: true }));

  // Auth Routes (Public)
  app.post("/api/auth/signup", (req, res) => authController.signup(req, res));
  app.post("/api/auth/login", (req, res) => authController.login(req, res));

  // File Routes (Protected)
  app.post("/api/files/upload", supabaseAuthMiddleware, (req, res) => 
    fileController.uploadFile(req, res)
  );
  app.get("/api/files", supabaseAuthMiddleware, (req, res) => 
    fileController.getFiles(req, res)
  );
  app.delete("/api/files/:id", supabaseAuthMiddleware, (req, res) => 
    fileController.deleteFile(req, res)
  );

  // Analytics Route (Protected)
  app.get("/api/analytics", supabaseAuthMiddleware, (req, res) => 
    analyticsController.getAnalytics(req, res)
  );

  const httpServer = createServer(app);
  return httpServer;
}

