import { Pool, neonConfig } from '@neondatabase/serverless';
import ws from 'ws';

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error('DATABASE_URL must be set');
}

const pool = new Pool({ connectionString: process.env.DATABASE_URL });

async function migrate() {
  try {
    console.log('Running database migrations...');

    await pool.query(`
      CREATE TABLE IF NOT EXISTS users (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        username TEXT NOT NULL UNIQUE,
        email TEXT NOT NULL UNIQUE,
        password TEXT NOT NULL,
        created_at TIMESTAMP DEFAULT NOW() NOT NULL
      );
    `);

    await pool.query(`
      CREATE TABLE IF NOT EXISTS files (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        user_id VARCHAR NOT NULL REFERENCES users(id) ON DELETE CASCADE,
        filename TEXT NOT NULL,
        file_type TEXT NOT NULL,
        file_size INTEGER NOT NULL,
        uploaded_at TIMESTAMP DEFAULT NOW() NOT NULL,
        signed_url TEXT
      );
    `);

    await pool.query(`
      CREATE INDEX IF NOT EXISTS idx_files_user_id ON files(user_id);
    `);

    await pool.query(`
      CREATE INDEX IF NOT EXISTS idx_files_uploaded_at ON files(uploaded_at DESC);
    `);

    console.log(' Database migrations completed successfully!');
    await pool.end();
    process.exit(0);
  } catch (error) {
    console.error(' Migration failed:', error);
    await pool.end();
    process.exit(1);
  }
}

migrate();

server/controllers/authController.ts.content:
import type { Request, Response } from 'express';
import { authService } from '../services/authService';
import { insertUserSchema, loginUserSchema } from '@shared/schema';
import { z } from 'zod';

export class AuthController {
  async signup(req: Request, res: Response) {
    try {
      const validatedData = insertUserSchema.parse(req.body);

      const result = await authService.signup(validatedData);

      const loginResult = await authService.login({
        email: validatedData.email,
        password: validatedData.password,
      });

      res.status(201).json({
        message: 'User created successfully',
        token: loginResult.token,
        user: result,
      });
    } catch (error: any) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: 'Validation error',
          details: error.errors.map(e => ({
            field: e.path.join('.'),
            message: e.message,
          })),
        });
      }

      const message = error.message || 'Signup failed';

      if (message.includes('already') || message.includes('duplicate')) {
        return res.status(409).json({ error: message });
      }

      res.status(500).json({ error: message });
    }
  }

  async login(req: Request, res: Response) {
    try {
      const validatedData = loginUserSchema.parse(req.body);

      const result = await authService.login(validatedData);

      res.json({
        message: 'Login successful',
        token: result.token,
        user: result.user,
      });
    } catch (error: any) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: 'Validation error',
          details: error.errors.map(e => ({
            field: e.path.join('.'),
            message: e.message,
          })),
        });
      }

      const message = error.message || 'Login failed';

      if (message.includes('Invalid') || message.includes('credentials')) {
        return res.status(401).json({ error: 'Invalid email or password' });
      }

      res.status(500).json({ error: message });
    }
  }
}

export const authController = new AuthController();

server/services/fileService.ts.content:
import { supabaseAdmin } from './supabase';
import { storage } from '../storage';
import type { InsertFile } from '@shared/schema';

export class FileService {
  private bucketName = 'user-files';

  async createSignedUploadUrl(userId: string, filename: string, fileType: string) {
    try {
      const filePath = `${userId}/${Date.now()}_${filename}`;

      const { data, error } = await supabaseAdmin.storage
        .from(this.bucketName)
        .createSignedUploadUrl(filePath);

      if (error) {
        throw new Error(error.message);
      }

      return {
        signedUrl: data.signedUrl,
        token: data.token,
        path: data.path,
        filePath,
      };
    } catch (error: any) {
      throw new Error(error.message || 'Failed to generate signed URL');
    }
  }

  async saveFileMetadata(fileData: InsertFile & { userId: string; signedUrl: string; storagePath: string }) {
    try {
      const file = await storage.createFile(fileData);
      return file;
    } catch (error: any) {
      throw new Error(error.message || 'Failed to save file metadata');
    }
  }

  async getUserFiles(userId: string) {
    try {
      const files = await storage.getFilesByUserId(userId);
      return files;
    } catch (error: any) {
      throw new Error(error.message || 'Failed to fetch user files');
    }
  }

  async deleteFile(fileId: string, userId: string) {
    try {
      const file = await storage.getFileById(fileId);

      if (!file) {
        throw new Error('File not found');
      }

      if (file.userId !== userId) {
        throw new Error('Unauthorized');
      }

      // Use the stored storage path for deletion (stored in signedUrl field)
      const storagePath = file.signedUrl || '';

      if (storagePath) {
        const { error } = await supabaseAdmin.storage
          .from(this.bucketName)
          .remove([storagePath]);

        if (error) {
          console.error('Error deleting file from storage:', error);
        }
      }

      await storage.deleteFile(fileId, userId);

      return true;
    } catch (error: any) {
      throw new Error(error.message || 'Failed to delete file');
    }
  }

  async createDownloadUrl(filePath: string, expiresIn: number = 3600) {
    try {
      const { data, error } = await supabaseAdmin.storage
        .from(this.bucketName)
        .createSignedUrl(filePath, expiresIn);

      if (error) {
        throw new Error(error.message);
      }

      return data.signedUrl;
    } catch (error: any) {
      throw new Error(error.message || 'Failed to create download URL');
    }
  }
}

export const fileService = new FileService();

