import { supabase, supabaseAdmin } from './supabase';
import type { InsertUser, LoginUser, User } from '@shared/schema';

export class AuthService {
  async signup(userData: InsertUser) {
    try {
      const { data, error } = await supabaseAdmin.auth.admin.createUser({
        email: userData.email,
        password: userData.password,
        email_confirm: true,
        user_metadata: {
          username: userData.username,
        },
      });

      if (error) {
        throw new Error(error.message);
      }

      if (!data.user) {
        throw new Error('Failed to create user');
      }

      return {
        id: data.user.id,
        email: data.user.email!,
        username: userData.username,
      };
    } catch (error: any) {
      throw new Error(error.message || 'Signup failed');
    }
  }

  async login(credentials: LoginUser) {
    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email: credentials.email,
        password: credentials.password,
      });

      if (error) {
        throw new Error(error.message);
      }

      if (!data.session || !data.user) {
        throw new Error('Login failed');
      }

      return {
        token: data.session.access_token,
        user: {
          id: data.user.id,
          email: data.user.email!,
          username: data.user.user_metadata?.username || '',
        },
      };
    } catch (error: any) {
      throw new Error(error.message || 'Login failed');
    }
  }

  async verifyToken(token: string) {
    try {
      const { data, error } = await supabase.auth.getUser(token);

      if (error) {
        throw new Error(error.message);
      }

      if (!data.user) {
        throw new Error('Invalid token');
      }

      return {
        id: data.user.id,
        email: data.user.email!,
        username: data.user.user_metadata?.username || '',
      };
    } catch (error: any) {
      throw new Error(error.message || 'Token verification failed');
    }
  }
}

export const authService = new AuthService();

server/middleware/supabaseAuth.ts.content:
import type { Request, Response, NextFunction } from 'express';
import { authService } from '../services/authService';

export interface AuthRequest extends Request {
  user?: {
    id: string;
    email: string;
    username: string;
  };
}

export const supabaseAuthMiddleware = async (
  req: AuthRequest,
  res: Response,
  next: NextFunction
) => {
  try {
    const authHeader = req.headers.authorization;

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return res.status(401).json({
        error: 'Unauthorized',
        message: 'Missing or invalid authorization header. Please provide a Bearer token.',
      });
    }

    const token = authHeader.substring(7);

    if (!token) {
      return res.status(401).json({
        error: 'Unauthorized',
        message: 'Token not provided',
      });
    }

    const user = await authService.verifyToken(token);

    req.user = user;
    next();
  } catch (error: any) {
    return res.status(401).json({
      error: 'Unauthorized',
      message: error.message || 'Invalid or expired token',
    });
  }
};

